//script para execu칞칚o de pagina칞칚o + mostrar card치pio 
function run(cardapio, paginacao) {
                cardapio = JSON.parse(cardapio)
                paginacao = JSON.parse(paginacao)

                let text = ''
                let inicio = paginacao * 5
                let fim = inicio + 5


                for(let i=inicio; i < fim; i++){
                    text += ` ${cardapio[i].id} - Pizza ${cardapio[i].sabor}: R$ ${cardapio[i].preco} reais <br/>` 
                }
                return text;  
            }

