//srcript para retornar o protocolo do pedido

function run(pedidoFinal) {
    pedidoFinal = JSON.parse(pedidoFinal)
    let sabores = ''
    let precoFinal = 0

    if (pedidoFinal.length > 0) {
        pedidoFinal.forEach(e => {
            sabores += `${e.sabor}, `
            precoFinal += parseInt(e.preco)
        })

        let precoFinal = precoFinal.toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' })

        return `Voc칡 escolheu ${sabores} e o valor final ficou R$ ${precoFinal}`

    } else {
        return "N칚o foi encontrado nenhum pedido neste protocolo."
    }
}
